import React, { FunctionComponent, Fragment } from 'react';
import { MethodAbi } from 'ethereum-types';
import { NavLeft, IconOptions } from '../reusable/ui';

interface Props {
    fxns: MethodAbi[]
    focused: string
    setFocused: (fxnName:string)=>void
}

export const TxSidePane: FunctionComponent<Props> = ({fxns, ...props}) => {
    return (
        <NavLeft searchable title={'Contract Methods'} menuItems={fxns.map((fxn) => {
            const icon:IconOptions= fxn.constant ? 'media' : 'add';
            return {
                title : fxn.name,
                selected : fxn.name === props.focused,
                icon : icon,
                onClick : ()=>{props.setFocused(fxn.name)}
            }
        })}/>
    )
}

export default TxSidePane;